import React from "react";
import { Box, Text, Stack, useColorMode } from "@chakra-ui/react";

// icons
import { BiMenu } from "react-icons/bi";
import { AiOutlineClose } from "react-icons/ai";


// fonts
import "@fontsource/montserrat";
import "@fontsource/poppins";
// import { ColorModeSwitcher } from "../ColorModeSwitcher";
// import { NavHashLink } from "react-router-hash-link";

// https://github.com/dimitrisraptis96/chakra-ui-navbar/blob/main/src/components/Header.js
// https://raptis.wtf/freebies/color-pairs

function Navbar(props) {
  // eslint-disable-next-line
  const { colorMode, toggleColorMode } = useColorMode();
  const [isOpen, setIsOpen] = React.useState(false);

  const toggle = () => setIsOpen(!isOpen);

  return (
    <NavBarContainer
      {...props}
      bg={colorMode === "light" ? "bodyBackground" : ""}
    >
      <LogoComponent />
      <MenuToggle toggle={toggle} isOpen={isOpen} />
      <MenuLinks isOpen={isOpen} />
    </NavBarContainer>
  );
}

const LogoComponent = () => {
  return (
    <a href="/">
      <Text as="h2" fontWeight="500" fontSize="2xl">
        Logo
      </Text>
    </a>
  );
};

const MenuToggle = ({ toggle, isOpen }) => {
  return (
    <Box display={{ base: "block", md: "none" }} onClick={toggle}>
      {isOpen ? <AiOutlineClose size={40} /> : <BiMenu size={40} />}
    </Box>
  );
};

const MenuLinks = ({ isOpen }) => {
  return (
    <Box
      display={{ base: isOpen ? "block" : "none", md: "block" }}
      flexBasis={{ base: "100%", md: "auto" }}
    >
      <Stack
        fontWeight="500"
        spacing={8}
        align={["flex-end", "center", "center", "center"]}
        justify={["center", "space-around", "space-around", "space-around"]}
        direction={["column", "row", "row", "row"]}
        pt={[4, 4, 0, 0]}
      >
        <a href="/">
          <Text display="block" fontWeight="500">
            Home
          </Text>
        </a>

        <a href="/#Products">
          <Text display="block" fontWeight="500">
            Products
          </Text>
        </a>

        <a href="/#Training">
          <Text display="block" fontWeight="500">
            {/* Training and Consultation */}
            Training
          </Text>
        </a>

        <a href="/#5s-Warm-Up">
          <Text display="block" fontWeight="500">
            5s Learn
          </Text>
        </a>

        {/* <ColorModeSwitcher /> */}
      </Stack>
    </Box>
  );
};

const NavBarContainer = ({ children, ...props }) => {
  return (
    <Box
      display={["flex", "grid", "grid", "grid"]}
      as="nav"
      // for small screen only
      justifyContent={["space-between", "space-between", "space-between"]}
      alignItems={["center", "center", "center", "center"]}
      w="100vw"
      p="1.25%"
      gridTemplateColumns={[
        "null",
        ".5fr 1fr .5fr",
        ".5fr 1fr .5fr",
        ".5fr 1fr .5fr",
      ]}
      fontFamily="montserrat"
      {...props}
    >
      {children}
    </Box>
  );
};

export default Navbar;
