import React from "react";
import {
  Box,
  Image,
  Text,
  extendTheme,
  ChakraProvider,
} from "@chakra-ui/react";
import { Keyboard, Pagination, Navigation } from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";
import AwesomeSlider from "react-awesome-slider";
import "react-awesome-slider/dist/styles.css";

import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

// carousel styles
import "swiper/css";

function Herocarousel() {
  // custom styles for carousel elements
  const styles = {
    mySwiper: {
      paddingBottom: "3rem",
      "& .swiper-pagination-bullet": {
        background: "blue",
      },
      "& .swiper-button-next:after": {
        fontSize: "2rem !important",
      },
      "& .swiper-button-prev:after": {
        fontSize: "2rem !important",
      },
    },
  };

  const theme = extendTheme({ styles });
  return (
    <>
      <ChakraProvider theme={theme}>
        <Swiper

          spaceBetween={10}
          breakpoints={{
            300: {
              slidesPerView: 1,
              spaceBetween: 10,
            },

            375: { slidesPerView: 1, spaceBetween: 20 },
            425: {
              slidesPerView: 1,
              spaceBetween: 20,
            },
            768: {
              slidesPerView: 1,
              spaceBetween: 20,
            },
            1024: {
              slidesPerView: 1,
              spaceBetween: 50,
            },
            1440: {
              slidesPerView: 1,
              spaceBetween: 20,
            },
            2560: {
              slidesPerView: 1,
              spaceBetween: 20,
            },
          }}
          keyboard={{
            enabled: true,
          }}
          loop={true}
          navigation={true}
          modules={[Pagination, Navigation]}
          className="mySwiper"
        >
          <SwiperSlide>
            {" "}
            <Box
              // border="1px solid"
              display="flex"
              justifyContent={["center", "center", "center", "center"]}
              padding="1rem"
            >
              <Image src="https://picsum.photos/200/300/?blur" boxSize="25%" />
              {/* <Image
              src="https://picsum.photos/200/300"
              boxSize="25%"
            /> */}
            </Box>
          </SwiperSlide>
          <SwiperSlide>
            <Box
              // border="1px solid"
              padding="1rem"
              display="flex"
              justifyContent={["center", "center", "center", "center"]}
            >
              <Image src="https://picsum.photos/200/300" boxSize="25%" />
            </Box>
          </SwiperSlide>
          <SwiperSlide>
            <Box
              // border="1px solid"
              padding="1rem"
              display="flex"
              justifyContent={["center", "center", "center", "center"]}
            >
              <Image
                src="https://picsum.photos/seed/picsum/200/300"
                boxSize="25%"
              />
            </Box>
          </SwiperSlide>
        </Swiper>
      </ChakraProvider>
      {/* </Box> */}
    </>
  );
}

export default Herocarousel;
