import RemoveIcon from "@mui/icons-material/Remove";
import AddIcon from "@mui/icons-material/Add";
import { useState } from "react";
import { useEffect } from "react";

const CartPageData = () => {
  return (
    <>
      <CartPageInitialComponent />
      <Box>
        <CartPageSecondryComponent />
      </Box>
    </>
  );
};

export default CartPageData;

const CartPageInitialComponent = () => {
  return (
    <>
      <Box
        display={"flex"}
        alignItems="center"
        justifyContent={"center"}
        padding={"1vmax"}
      >
        <Text fontSize={"5xl"} fontWeight={"bold"}>
          Cart Items
        </Text>
      </Box>
    </>
  );
};

const CartPageSecondryComponent = () => {
  return (
    <>
      <Flex w={"100vw"} gap={"2%"} px={"2%"} py={"2%"}>
        <CartItems />
        <CartPaymentPage />
      </Flex>
    </>
  );
};

const CartItems = () => {
  const {
    state: { cart },
    dispatch,
  } = CartState();
  const { isOpen, onOpen, onClose } = useDisclosure();
  const toast = useToast();
  return (
    <Flex
      boxShadow="inset 0 0 0 1px #e9e9eb"
      rounded={"md"}
      padding={"1rem"}
      w={"60%"}
      flexWrap={"wrap"}
      flexDirection={"column"}
      gap={"1rem"}
    >
      {cart.map((item, index) => {
        // console.log(item);
        return (
          <Flex
            boxShadow="inset 0 0 0 1px #e9e9eb"
            rounded={"md"}
            padding={"1vmax"}
            gap={"1rem"}
            py={"1rem"}
            key={index}
          >
            <Box>
              <Image
                src={item.image}
                alt={"image"}
                objectFit={"contain"}
                height={"auto"}
                width={"md"}
                rounded={"md"}
              />
            </Box>
            <Flex
              flexDirection={"column"}
              w="100%"
              padding={".25rem"}
              justifyContent={"space-between"}
            >
              {/* title */}
              <Box>
                <Text
                  fontSize={"xl"}
                  fontWeight={"semibold"}
                  _hover={{
                    color: "orange.500",
                    cursor: "pointer",
                  }}
                  onClick={() => {
                    onOpen();

                    // for toast closing
                    function close() {
                      toast.closeAll();
                    }
                    toast({
                      // position: "bottom-left",
                      duration: "200000000",
                      isClosable: true,
                      render: () => (
                        <Collapse in={isOpen} animateOpacity>
                          <Modal
                            isCentered
                            isOpen={isOpen}
                            onClose={onClose}
                            size={"3xl"}
                            blockScrollOnMount={false}
                          >
                            <ModalOverlay
                              bg="blackAlpha.200"
                              backdropFilter="blur(.1em)"
                            />
                            <ModalContent>
                              <ModalHeader
                                alignItems={"center"}
                                display="flex"
                                justifyContent={"flex-end"}
                              >
                                <Button onClick={close}>
                                  <CloseIcon />
                                </Button>
                              </ModalHeader>

                              <ModalBody>
                                <Flex gap={"1rem"}>
                                  {/* img */}
                                  <Box width={"xl"}>
                                    <Image
                                      src={item.image}
                                      alt={item.name}
                                      rounded={"md"}
                                    />
                                  </Box>
                                  <Stack
                                    padding={"2"}
                                    width={"xl"}
                                    border="1px solid lightgrey"
                                    rounded={"md"}
                                  >
                                    {/* title */}
                                    <Box>
                                      <Text fontSize={"3xl"}>{item.name}</Text>
                                    </Box>
                                    {/* price */}
                                    <Box
                                      display={"flex"}
                                      alignItems={"center"}
                                      gap={".25rem"}
                                    >
                                      <Text
                                        textDecoration={"line-through"}
                                        fontSize={"md"}
                                      >
                                        ₹{item.price * 2}
                                      </Text>
                                      <Text fontSize={"xl"} color={"#e97730"}>
                                        ₹{item.price}
                                      </Text>
                                    </Box>
                                    {/* ratings */}
                                    <Box>
                                      <ReactStars
                                        size={20}
                                        value={item.ratings}
                                        edit={false}
                                      />
                                    </Box>
                                    {/* descriptions */}
                                    <Box>
                                      <Text>{item.description}</Text>
                                    </Box>
                                  </Stack>
                                </Flex>
                              </ModalBody>
                              <ModalFooter>
                                {/* <Button onClick={close}></Button> */}
                                {cart.some((data) => data.id === item.id) ? (
                                  <Button
                                    onClick={() => {
                                      dispatch({
                                        type: "REMOVE_FROM_CART",
                                        payload: item,
                                      });
                                    }}
                                    aria-label="left-arrow"
                                    borderRadius="md"
                                    variant="ghost"
                                    colorScheme="red"
                                  >
                                    Remove from Cart
                                  </Button>
                                ) : (
                                  <Button
                                    onClick={() => {
                                      dispatch({
                                        type: "ADD_TO_CART",
                                        payload: item,
                                      });
                                    }}
                                    aria-label="left-arrow"
                                    borderRadius="md"
                                    variant="ghost"
                                    _hover={{
                                      // opacity: "1",
                                      bg: "orange.500",
                                      color: "white",
                                      display: "block",
                                    }}
                                  >
                                    Add to Cart
                                  </Button>
                                )}
                              </ModalFooter>
                            </ModalContent>
                          </Modal>
                        </Collapse>
                      ),
                    });
                  }}
                >
                  {item.name}
                </Text>
              </Box>
              {/* description */}
              <Box>
                <Text noOfLines={"1"}>{item.description}</Text>
              </Box>
              {/* category */}
              <Box>
                <Text fontSize={"lg"} fontWeight={"thin"} color={"orange.500"}>
                  <Tag
                    size={"md"}
                    color={"orange.500"}
                    rounded={"full"}
                    bg={"transparent"}
                    border={"1px solid"}
                  >
                    {item.category}
                  </Tag>
                </Text>
              </Box>
              {/* price */}
              <Box display={"flex"} gap={".5rem"}>
                <Text>MRP</Text>
                <Text textDecoration={"line-through"}>₹{item.price * 2}</Text>
                <Text fontWeight={"extrabold"}>₹{item.price}</Text>
                <Text color={"orange.500"}>{item.discount}% OFF</Text>
              </Box>
              {/* buttons */}
              <Box
                display={"flex"}
                alignItems={"center"}
                justifyContent={"space-between"}
              >
                <Button
                  bg={"transparent"}
                  border={"1px solid red"}
                  color={"red"}
                  onClick={() => {
                    dispatch({
                      type: "REMOVE_FROM_CART",
                      payload: item,
                    });
                  }}
                  _hover={{
                    bg: "red",
                    color: "white",
                  }}
                >
                  <Text fontWeight={"light"}>Remove From Cart</Text>
                </Button>
                <Box display={"flex"} gap={"2rem"}>
                  <Box
                    bg={"transparent"}
                    color={"orange.500"}
                    border={"1px solid "}
                    display={"flex"}
                    alignItems={"center"}
                    rounded={"full"}
                    padding={"1"}
                    _hover={{
                      color: "white",
                      bg: "orange.500",
                      cursor: "pointer",
                    }}
                  >
                    <AddIcon />
                  </Box>
                  <Box
                    bg={"transparent"}
                    display={"flex"}
                    alignItems={"center"}
                    color={"orange.500"}
                    border={"1px solid "}
                    rounded={"full"}
                    padding={"1"}
                    _hover={{
                      color: "white",
                      bg: "orange.500",
                      cursor: "pointer",
                    }}
                  >
                    <RemoveIcon />
                  </Box>
                </Box>
              </Box>
            </Flex>
          </Flex>
        );
      })}
    </Flex>
  );
};

const CartPaymentPage = () => {
  const {
    state: { cart },
    dispatch,
  } = CartState();
  const [Total, setTotal] = useState();
  const [Discount, setDiscount] = useState();
  const [TotalAmount, setTotalAmount] = useState();

  useEffect(() => {
    // get MRP
    setTotal(cart?.reduce((acc, curr) => acc + Number(curr.price), 0));
    setDiscount(
      cart?.reduce((acc, curr) => Math.floor(acc + Number(curr.price)) / 8, 0)
    );
    setTotalAmount(Math.florr(Total - Discount));
  }, [cart]);

  return (
    <>
      <Box
        w={"20vw"}
        height={"60vh"}
        position={"fixed"}
        right={"10%"}
        boxShadow="inset 0 0 0 1px #e9e9eb"
        rounded={"md"}
        padding={"1vmax"}
      >
        <Flex flexDirection={"column"} gap={"3rem"}>
          {/* // subtotal */}
          <Box
            display={"flex"}
            justifyContent={"space-between"}
            alignItems={"center"}
            w={"100%"}
            px={"4%"}
          >
            <Text fontSize={"lg"}>Sub Total :</Text>
            <Text fontWeight={"bold"} fontSize={"lg"}>
              {cart.length} Items
            </Text>
          </Box>
          {/* Total Mrp */}
          <Box
            display={"flex"}
            justifyContent={"space-between"}
            alignItems={"center"}
            w={"100%"}
            px={"4%"}
          >
            <Text fontSize={"lg"}>Total MRP</Text>
            <Text fontSize={"lg"} color={"orange.500"}>
              $ {Total}
            </Text>
          </Box>
          {/* discount on mrp */}
          <Box
            display={"flex"}
            justifyContent={"space-between"}
            alignItems={"center"}
            w={"100%"}
            px={"4%"}
          >
            <Text fontSize={"lg"}>Discount</Text>
            <Text fontSize={"lg"} color={"orange.500"}>
              ${Discount}
            </Text>
          </Box>
          {/* convinience fee */}
          <Box
            display={"flex"}
            justifyContent={"space-between"}
            alignItems={"center"}
            w={"100%"}
            px={"4%"}
          >
            <Text fontSize={"lg"}>Convinience Fee</Text>
            <Text fontSize={"lg"} color={"orange.500"}>
              Free
            </Text>
          </Box>
          {/* total amount */}
          <Box
            display={"flex"}
            justifyContent={"space-between"}
            alignItems={"center"}
            w={"100%"}
            px={"4%"}
          >
            <Text fontSize={"lg"}>Total Amount</Text>
            <Text fontSize={"lg"} color={"orange.500"}>
              ${TotalAmount}
            </Text>
          </Box>
          {/* buttons */}
          <Box px={"4%"}>
            <Button
              w={"100%"}
              bg={"orange.500"}
              color={"white"}
              fontWeight={"light"}
            >
              Place Order
            </Button>
          </Box>
        </Flex>
      </Box>
    </>
  );
};
