import React from "react";
import { Box, Flex } from "@chakra-ui/react";
import FilterComponent from "./FilterComponent";
import CartItems from "./CartItems";

const FullComponent = () => {
  return (
    <>
      <Flex
        pos={"absolute"}
        px="1rem"
        py="1rem"
        gap={'1rem'}  
        top={{ xl: "80%", lg: "80%",md:'80%',base:'90%' }}
        w="100%"
      >
        <FilterPortion />
        <CartItemsPortion />
      </Flex>
    </>
  );
};

export default FullComponent;

const FilterPortion = ({ children, ...props }) => {
  return (
    <>
      <Flex
        {...props}
        bg="pink.50"
        borderRadius={"md"}
        border={"1px solid"}
        w="20vmax"
        minH={'20vh'}
        bottom="80vmax"
        position="fixed" 
        top="37vmax"

      >
        <Box>
          <FilterComponent />
        </Box>
      </Flex>
    </>
  );
};

const CartItemsPortion = ({ children, ...props }) => {
  return (
    <>
      <Flex
        {...props}
        bg="whiteAlpha.200"
        borderRadius={"md"}
        // border={"1px solid"}
        w={"75vmax"}
        // minH={"0vh"}
        pos="relative"
        left={'22%'}
      >
        <Box>
          <CartItems />
        </Box>
      </Flex>
    </>
  );
};
