import React from "react";
import { Box, Flex } from "@chakra-ui/react";
import FilterComponent from "./FilterComponent";
import CartItems from "./CartItems";

const FullComponent = () => {
  return (
    <>
      <Flex
        pos={"absolute"}
        // bottom={{ xl: "8rem", lg: "7rem", md: "6rem", base: "4rem" }}
        top={{ xl: "80%",lg:'' }}
        w="100%"
      >
        <FilterPortion />
        <CartItemsPortion />
      </Flex>
    </>
  );
};

export default FullComponent;

const FilterPortion = ({ children, ...props }) => {
  return (
    <>
      <Flex
        {...props}
        bg="whiteAlpha.600"
        borderRadius={"md"}
        border={"1px solid"}
        w="20vw"
        minH={"45vh"}
      >
        <Box>
          <FilterComponent />
        </Box>
      </Flex>
    </>
  );
};

const CartItemsPortion = ({ children, ...props }) => {
  return (
    <>
      <Flex
        {...props}
        bg="whiteAlpha.600"
        borderRadius={"md"}
        border={"1px solid"}
        w={"80vw"}
        minH={"90vh"}
      >
        <Box>
          <CartItems />
        </Box>
      </Flex>
    </>
  );
};
