import React from "react";
import { CartState } from "../Context/context";
import {
  Box,
  Image,
  Text,
  Heading,
  Stack,
  Button,
  useToast,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
} from "@chakra-ui/react";
import { Eye } from "@styled-icons/feather/Eye";

const CartItems = () => {
  const {
    state: { products, cart },
    dispatch,
  } = CartState();
  console.log(products);
  const toast = useToast();
  const toastIdRef = React.useRef();
  const { isOpen, onOpen, onClose } = useDisclosure();

  return (
    <>
      <Box
        padding="1%"
        display="flex"
        flexWrap="wrap"
        justifyContent={"space-around"}
        alignItems="center"
      >
        {products.map((item, index) => {
          return (
            <Box key={index} margin="2%">
              <Image
                src={item.image}
                height={230}
                width={282}
                rounded={"md"}
                objectFit={"cover"}
                transform="scale(1.0)"
                _hover={{
                  transform: "scale(1.05)",
                  transition: "0.3s ease-in-out",
                }}
                zIndex="2"
              />
              <Stack pt={5} align={"center"}>
                <Text
                  color={"gray.500"}
                  fontSize={"sm"}
                  textTransform={"uppercase"}
                >
                  {item.category}
                </Text>
                <Heading fontSize={"xl"} fontWeight={500}>
                  {item.name}
                </Heading>
                <Stack display="flex" direction={"row"}>
                  <Box display={"flex"} alignItems="center" gap="2">
                    <Text fontWeight={800} fontSize={"xl"}>
                      ${item.price * 2}
                    </Text>
                    <Text textDecoration={"line-through"} color={"gray.600"}>
                      ${item.price}
                    </Text>
                  </Box>
                  {cart.some((data) => data.id === item.id) ? (
                    <Button
                      onClick={() => {
                        dispatch({
                          type: "REMOVE_FROM_CART",
                          payload: item,
                        });
                      }}
                      aria-label="left-arrow"
                      borderRadius="md"
                      variant="ghost"
                      colorScheme="red"
                    >
                      Remove from Cart
                    </Button>
                  ) : (
                    <Button
                      onClick={() => {
                        dispatch({
                          type: "ADD_TO_CART",
                          payload: item,
                        });
                      }}
                      aria-label="left-arrow"
                      borderRadius="md"
                      variant="ghost"
                      _hover={{
                        // opacity: "1",
                        bg: "orange.500",
                        color: "white",
                        display: "block",
                      }}
                    >
                      Add to Cart
                    </Button>
                  )}
                </Stack>
                <Box
                  pos={"relative"}
                  display="flex"
                  left={"15%"}
                  bottom={"250"}
                >
                  <Button
                    opacity="0"
                    onClick={
                      (() => 
                      onOpen(),
                      toast({

                        // position: "bottom-left",
                        duration: "2000",
                        isClosable: true,
                        render: () => (
                          <Modal
                            isCentered
                            onClose={onClose}
                            isOpen={isOpen}
                            motionPreset="slideInBottom"
                          >
                            <ModalOverlay
                              bg="blackAlpha.200"
                              backdropFilter="blur(.5em)"
                            />
                          
                          </Modal>
                        ),
                      }))
                    }
                    pos={"absolute"}
                    right={"5"}
                    aria-label="left-arrow"
                    borderRadius="md"
                    variant="ghost"
                    _hover={{
                      opacity: "1",
                      color: "white",
                      bg: "orange.500",
                      display: "block",
                    }}
                  >
                    <Eye size={24} />
                  </Button>
                </Box>
              </Stack>
            </Box>
          );
        })}
      </Box>
    </>
  );
};

export default CartItems;
