import React, { useState, useEffect } from "react";
import Product from "./product.json";
import {
  Box,
  Image,
  Text,
  useColorModeValue,
  Heading,
  Stack,
} from "@chakra-ui/react";

const CartItems = () => {
  const [Prod, setProd] = useState([]);
  const FetchPost = () => {
    let CalculatedProd = [];
    if (Product.length > 0) {
      for (let index = 0; index < 12; index++) {
        CalculatedProd = [...CalculatedProd, Product[index]];
      }
    }
    // console.log(CalculatedProd);

    setProd(CalculatedProd);
  };
  useEffect(() => {
    return () => {
      FetchPost();
    };
  }, []);

  return (
    <>
      <Box borderRadius={'md'} padding={'1rem'} border="1px solid" bg={'white'}>
        {/* {Prod.map((item, index) => {
        return <Box key={index} display={'flex'}>
          <ProductSimple />
        </Box>;
      })} */}
        <ProductSimple />
      </Box>
    </>
  );
};

