import React from "react";
import {
  Box,
  Heading,
  Text,
  Stack,
  Avatar,
  Image,
  Tag,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  const Item = HashnodeData.posts;
  let DisplaythreePost = [];
  if (Item.length > 0) {
    for (let index = 0; index < 3; index++) {
      DisplaythreePost = [...DisplaythreePost, Item[index]];
    }
  }

  return (
    <>
      <Box
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        py={{ xl: "4%", lg: "4%" }}
        pt={{ sm: "1rem", md: "1rem" }}
        display={{ sm: "flex", md: "grid" }}
        // border="1px solid"
        // margin="0 auto"
        pl={{ xl: "1rem", lg: "1rem" }}
        pr={{ xl: "1rem", lg: "1rem" }}
        flexDirection={{ sm: "column" }}
        gridTemplateColumns={{
          md: "repeat(2, 1fr)",
          lg: "repeat(3, 1fr)",
          xl: "repeat(3, 1fr)",
        }}
        // justifyContent={'space-between'}
        padding={{ sm: "1rem", md: ".2rem" }}
        // mx={{ xl: "10%", lg: "10%", md: "10%" }}
        gap={10}
      >
        {DisplaythreePost.map((data, index) => {
          return (
            <>
              <Stack
                key={data}
                border="1px solid #E7EBF0"
                rounded={"md"}
                p={4}
                overflow={"hidden"}
              >
             
              </Stack>
            </>
          );
        })}
        {/* </Box> */}
      </Box>
    </>
  );
};

export default BlogSection;
