import { FormEvent, ChangeEvent, useState } from "react";
import {
  Stack,
  FormControl,
  Input,
  Button,
  useColorModeValue,
  Heading,
  Text,
  Container,
  Flex,
  Box,
} from "@chakra-ui/react";
import { CheckIcon } from "@chakra-ui/icons";

export default function Simple() {
  const [error, setError] = useState(false);

  return (
    <Flex
      minH={"40vh"}
      align={"center"}
      justify={"center"}
      mx={{ xl: "10%", lg: "10%", md: "10%" }}
      // flexDirection={{md:'column'}}
      // my={{ xl: "10%", lg: "10%", md: "10%" }}
      border="1px solid"
    >
      <Container
        maxW={"md"}
        bg={useColorModeValue("white", "whiteAlpha.100")}
        // boxShadow={"xl"}
        rounded={"lg"}
        p={{ xl: "6", lg: "5", md: "4" }}
        direction={"column"}
      >
        <Heading
          as={"h2"}
          fontSize={{ base: "xl", sm: "2xl" }}
          textAlign={"center"}
          mb={5}
        >
          Subscribe to our Newsletter
        </Heading>
        <Box
          display={["flex"]}
          direction={{ sm: "row", md: "row" }}
          as={"form"}
          border="1px solid"
        >
          <FormControl>
            <Input
              variant={"solid"}
              borderWidth={1}
              color={"gray.800"}
              _placeholder={{
                color: "gray.400",
              }}
              borderColor={useColorModeValue("gray.300", "gray.700")}
              id={"email"}
              type={"email"}
              required
              placeholder={"Your Email"}
              aria-label={"Your Email"}
            />
          </FormControl>
          <FormControl>
            <Button colorScheme="orange">
              <CheckIcon />
            </Button>
          </FormControl>
        </Box>
        <Text
          mt={2}
          textAlign={"center"}
          color={error ? "red.500" : "gray.500"}
        >
          {error
            ? "Oh no an error occured! üò¢ Please try again later."
            : "You won't receive any spam! ‚úåÔ∏è"}
        </Text>
      </Container>
    </Flex>
  );
}
