import {
  Image,
  Box,
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
} from "@chakra-ui/react";
import { ChevronRightIcon } from "@chakra-ui/icons";
import NatureImg from "../../Assets/temp-img/7.jpg";

const BannerComponent = () => {
  return (
    <>
      <Box mx="5%">
        <Image
          src={NatureImg}
          objectFit={"cover"}
          width="100vw"
          height={"50vh"}
          alt="banner"
        />
        <Breadcrumb
          spacing="8px"
          separator={<ChevronRightIcon color="gray.500" />}
        >
          <BreadcrumbItem>
            <BreadcrumbLink href="#">Home</BreadcrumbLink>
          </BreadcrumbItem>

          <BreadcrumbItem>
            <BreadcrumbLink href="#">About</BreadcrumbLink>
          </BreadcrumbItem>

          <BreadcrumbItem isCurrentPage>
            <BreadcrumbLink href="#">Contact</BreadcrumbLink>
          </BreadcrumbItem>
        </Breadcrumb>
      </Box>
      <MessageComponent />
    </>
  );
};
export default BannerComponent;

const MessageComponent = () => {
  return (
    <>
      <Box
        // width={{ xl: "100px", lg: "80px", md: "60px", sm: "40px" }}
        width={{ xl: "20%", lg: "15%", md: "20pc", base:'15pc'}}
        // height={{ xl: "80%", lg: "50%", md: "40%", sm: "30%" }}
        height={{ xl: "20pc", lg: "15pc", md: "20pc", base:'15pc' }}
        bg={"whiteAlpha.500"}
        border="1px solid"
        borderRadius="50%"
      >
        
      </Box>
    </>
  );
};
