import React from "react";

import {
  Box,
  Heading,
  Text,
  Stack,
  Image,
  useToast,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  Button,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";
import { Eye } from "@styled-icons/feather/Eye";

function ProdContainerItem() {
  const { isOpen, onOpen, onClose } = useDisclosure();
  // const [overlay, setOverlay] = React.useState(<OverlayTwo />);
  let ShowcaseProduct = [];
  // console.log(productsArray);
  const Item = HashnodeData.posts;
  if (Item.length > 0) {
    for (let index = 0; index < 6; index++) {
      ShowcaseProduct = [...ShowcaseProduct, Item[index]];
    }
  }
  const toast = useToast();

  return (
    <>
      <Box
        display={{ xl: "grid", lg: "grid", md: "grid", sm: "flex" }}
        flexWrap="wrap"
        gridTemplateColumns={{
          xl: ".5fr .5fr .5fr",
          lg: ".5fr .5fr .5fr",
          md: ".5fr .5fr .5fr",
        }}
        placeItems="center"
        // alignItems=""
        justifyContent={"space-between"}
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        // py={{ xl: "4%", lg: "4%" }}
        // pt={{ sm: "1rem", md: "1rem" }}
        // border="1px solid"
        p={["1rem"]}
      >
        {ShowcaseProduct.map(function ({ title, id, coverImage, brief }) {
          return (
            <Box key={id}>
              <Box
                role={"group"}
                // p={6}
                // maxW={}
                rounded={"md"}
                overflow={"hidden"}
                // pos={"relative"}
                // mb={"2%"}
                border="1px solid lightgrey"
                zIndex={1}
                mb={["2rem"]}
              >
                <Box
                  overflow={"hidden"}
                  // rounded={"md"}
                  h={"250px"}
                  w={["100%", "180px", "240px", "280px"]}
                  bg={"gray.100"}
                  // mt={-6}
                  // mx={-6}
                  // mb={6}
                  pos={"relative"}
                >
                  <Button
                    display="none"
                    pos={"absolute"}
                    aria-label="left-arrow"
                    left={"40%"}
                    top={"40%"}
                    transform={"translate(0%, 50%)"}
                    borderRadius="md"
                    zIndex={2}
                    variant="ghost"
                    color="#fff"
                    // _hover={{ color: "black", bg: "white" }}
                    // ml="4"
                    onClick={() => {
                      onOpen();
                      function close() {
                        toast.closeAll();
                      }
                      toast({
                        position: "bottom-left",
                        duration: "200000000",
                        isClosable: true,
                        render: () => (
                          <Modal isCentered isOpen={isOpen} onClose={onClose}>
                            <ModalOverlay
                              bg="blackAlpha.200"
                              backdropFilter="blur(.5em)"
                            />
                            <ModalContent>
                              <ModalHeader>
                                <Box>
                                  <Image
                                    src={coverImage}
                                    borderRadius="md"
                                    alt={title}
                                    width="100%"
                                    height="100%"
                                  />
                                </Box>
                              </ModalHeader>
                              {/* <IconButton onClick={}>
                                <CloseIcon />
                              </IconButton> */}
                              {/* <ModalCloseButton /> */}
                              <ModalBody>
                                <Text fontSize={"2xl"} fontWeight={"bold"}>
                                  {title}
                                </Text>

                                <Text py="5">{brief}</Text>
                              </ModalBody>
                              <ModalFooter>
                                <Button onClick={close}>Close</Button>
                              </ModalFooter>
                            </ModalContent>
                          </Modal>
                        ),
                      });
                      // onClose();
                      // eslint-disable-next-line no-lone-blocks
                    }}
                    _groupHover={{
                      color: "white",
                      bg: "orange.500",
                      display: "block",
                    }}
                    // transform={"translate(0%, 50%)"}
                  >
                    <Eye size={24} />
                  </Button>

                  <Image
                    // rounded={"md"}
                    transform="scale(1.0)"
                    height={"100%"}
                    width={"100%"}
                    objectFit={"cover"}
                    src={coverImage}
                    _hover={{
                      transform: "scale(1.05)",
                      transition: "0.3s ease-in-out",
                    }}
                  />
                </Box>
                <Stack>
                  <Text
                    color={"gray.500"}
                    fontSize={"sm"}
                    textTransform={"uppercase"}
                  >
                    {title}
                  </Text>
                  <Stack direction={"row"} align={"center"}>
                    <Text fontWeight={800} fontSize={"xl"}>
                      $57
                    </Text>
                    <Text textDecoration={"line-through"} color={"gray.600"}>
                      $199
                    </Text>
                  </Stack>
                </Stack>
              </Box>
            </Box>
          );
        })}
      </Box>
    </>
  );
}

const ProdContainerHeader = () => {
  return (
    <>
      <Box
        // px="1.5rem"
        py="1.5%"
        mx={{ base: "10%" }}
        display="flex"
        justifyContent={["center", "flex-start", "flex-start", "flex-start"]}
      >
        <Heading
          fontSize={["1.5rem", "1.75rem"]}
          fontWeight={["500", "600"]}
          as="h2"
          color="#2c2c2c"
        >
          <Box display={["inline-block"]}>Hot Products</Box>
          <Box
            borderBottom="2px solid #e97730"
            py={["1"]}
            pos={["relative"]}
            w={["70%", "60%"]}
            left={["4", "4"]}
            right={["4", "4"]}
          ></Box>
        </Heading>
      </Box>
    </>
  );
};

function TrendingProduct(props) {
  // eslint-disable-next-line

  return (
    <>
      <ProdContainer {...props}>
        <ProdContainerHeader />
        <ProdContainerItem />
      </ProdContainer>
    </>
  );
}

const ProdContainer = ({ children, ...props }) => {
  return <Box w="100vw">{children}</Box>;
};

export default TrendingProduct;
