import React, { useState, useEfect } from "react";
import {
  Box,
  Center,
  Heading,
  Text,
  Stack,
  Avatar,
  Image,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  // const [posts, setposts] = useState([]);
  //   console.log(HashnodeData.posts)
  const Item = HashnodeData.posts;
  let DisplaythreePost = [];
  if (Item.length > 0) {
    for (let index = 0; index < 3; index++) {
      DisplaythreePost = [...DisplaythreePost, Item[index]];
    }
  }

  return (
    <>
      <Box
        display={{ xl: "flex" }}
        justifyContent={{ xl: "space-between" }}
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        py={{ xl: "5%", lg: "5%", md: "5%" }}
      >
        {DisplaythreePost.map((data) => {
          return (
            <>
              <Box
                maxW={"350px"}
                w={"md"}
                border="1px solid #E7EBF0"
                // bg={useColorModeValue("white", "gray.900")}
                // boxShadow={"2xl"}
                rounded={"md"}
                p={6}
                overflow={"hidden"}
              >
                <Box
                  h={"210px"}
                  bg={"gray.100"}
                  mt={-6}
                  mx={-6}
                  mb={6}
                  pos={"relative"}
                >
                  <Image src={data.coverImage} layout={"fill"} h="" />
                </Box>
                <Stack>
                  <Text
                    color={"green.500"}
                    textTransform={"uppercase"}
                    fontWeight={800}
                    fontSize={"sm"}
                    letterSpacing={1.1}
                  >
                    Tag
                  </Text>
                  <Heading
                    // color={useColorModeValue("gray.700", "white")}
                    fontSize={"2xl"}
                    // fontFamily={"body"}
                  >
                    Boost your conversion rate
                  </Heading>
                  <Text color={"gray.500"}>
                    Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed
                    diam nonumy eirmod tempor invidunt ut labore et dolore magna
                    aliquyam erat, sed diam voluptua. At vero eos et accusam et
                    justo duo dolores et ea rebum.
                  </Text>
                </Stack>
                <Stack mt={6} direction={"row"} spacing={4} align={"center"}>
                  <Avatar
                    src={"https://avatars0.githubusercontent.com/u/1164541?v=4"}
                    alt={"Author"}
                  />
                  <Stack direction={"column"} spacing={0} fontSize={"sm"}>
                    <Text fontWeight={600}>Achim Rolle</Text>
                    <Text color={"gray.500"}>Feb 08, 2021 · 6min read</Text>
                  </Stack>
                </Stack>
              </Box>
            </>
          );
        })}
      </Box>
    </>
  );
};

export default BlogSection;

