import React, { useState, useEffect } from "react";
import Product from "./product.json";
import {
  Box,
  Image,
  Text,
  // useColorModeValue,
  Heading,
  Stack,
  Button,
} from "@chakra-ui/react";

import { Eye } from "@styled-icons/feather/Eye";

const CartItems = () => {
  const [Prod, setProd] = useState([]);
  const FetchPost = () => {
    let CalculatedProd = [];
    if (Product.length > 0) {
      for (let index = 0; index < 12; index++) {
        CalculatedProd = [...CalculatedProd, Product[index]];
      }
    }
    // console.log(CalculatedProd);

    setProd(CalculatedProd);
  };
  useEffect(() => {
    return () => {
      FetchPost();
    };
  }, []);

  return (
    <>
      <Box
        padding="1%"
        display="flex"
        flexWrap="wrap"
        justifyContent={"space-around"}
        alignItems="center"
      >
        {Prod.map((item, index) => {
          return (
            <Box key={index} margin="2%">
              <Image
                src={item.images}
                height={230}
                width={282}
                objectFit={"cover"}
                zIndex="2"
              />
              <Stack pt={5} align={"center"}>
                <Text
                  color={"gray.500"}
                  fontSize={"sm"}
                  textTransform={"uppercase"}
                >
                  {item.category.name}
                </Text>
                <Heading fontSize={"xl"} fontFamily={"body"} fontWeight={500}>
                  {item.title}
                </Heading>
                <Stack direction={"row"} align={"center"}>
                  <Text fontWeight={800} fontSize={"xl"}>
                    ${item.price * 2}
                  </Text>
                  <Text textDecoration={"line-through"} color={"gray.600"}>
                    ${item.price}
                  </Text>
                </Stack>
                <Box
                  pos={"relative"}
                  display="flex"
                  left={"15%"}
                  bottom={"250"}
                >
                  <Button
                    // display="none"
                    pos={"absolute"}
                    right={"5"}
                    aria-label="left-arrow"
                    borderRadius="md"
                    zIndex="5"
                    variant="ghost"
                    _hover={{
                      color: "white",
                      bg: "orange.500",
                      display: "block",
                    }}
                  >
                    <Eye size={24} />
                  </Button>
                </Box>
              </Stack>
            </Box>
          );
        })}
      </Box>
    </>
  );
};

export default CartItems;
