import React, { useState, useEfect } from "react";
import {
  Box,
  Heading,
  Link,
  Image,
  Grid,
  Text,
  Divider,
  HStack,
  Tag,
  Wrap,
  WrapItem,
  SpaceProps,
  useColorModeValue,
  Container,
  VStack,
  GridItem,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  // const [posts, setposts] = useState([]);
  //   console.log(HashnodeData.posts)
  const Item = HashnodeData.posts;
  let DisplaythreePost = [];
  if (Item.length > 0) {
    for (let index = 0; index < 3; index++) {
      DisplaythreePost = [...DisplaythreePost, Item[index]];
    }
  }

  return (
    <>
      {DisplaythreePost.map((data) => {
        console.log(data);
        return (
          <Box key={data.id} mx={{ xl: "10%", lg: "10%", md: "10%" }}>
            <Grid
              border="1px solid"
              display={"grid"}
              gridTemplateColumns={{
                xl: ".5fr .5fr .5fr",
                lg: ".5fr .5fr .5fr",
                md: ".5fr .5fr .5fr",
              }}
            >
              <GridItem>
               
              </GridItem>
            </Grid>
          </Box>
        );
      })}
    </>
  );
};

export default BlogSection;

const SingleBlog = ({ data }) => {
  return (
    <Box borderRadius="lg" overflow="hidden" displa>
      {/* <Link textDecoration="none" _hover={{ textDecoration: "none" }}> */}
      <Image
        transform="scale(1.0)"
        src={data.coverImage}
        alt={data.slug}
        objectFit="cover"
        width="25%"
        transition="0.3s ease-in-out"
        _hover={{
          transform: "scale(1.05)",
        }}
      />
      {/* </Link> */}
    </Box>
  );
};
