import React, { useState, useEfect } from "react";
import {
  Box,
  Center,
  Heading,
  Text,
  Stack,
  Avatar,
  Image,
  Tag,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  // const [posts, setposts] = useState([]);
  //   console.log(HashnodeData.posts)
  const Item = HashnodeData.posts;
  let DisplaythreePost = [];
  if (Item.length > 0) {
    for (let index = 0; index < 3; index++) {
      DisplaythreePost = [...DisplaythreePost, Item[index]];
    }
  }

  return (
    <>
      <Box
        display={{ xl: "flex", lg: "flex" }}
        justifyContent={{ xl: "space-between", lg: "space-between" }}
        height=""
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        py={{ xl: "5%", lg: "5%", md: "5%" }}
      >
        {DisplaythreePost.map((data) => {
          console.log(data);
          return (
            <>
              <Box
                maxW={"350px"}
                w={"md"}
                border="1px solid #E7EBF0"
                // bg={useColorModeValue("white", "gray.900")}
                // boxShadow={"2xl"}
                rounded={"md"}
                p={6}
                overflow={"hidden"}
              >
                <Box
                  h={"220px"}
                  bg={"gray.100"}
                  mt={-6}
                  mx={-6}
                  mb={6}
                  pos={"relative"}
                >
                  <Image
                    src={data.coverImage}
                    layout={"cover"}
                    h="100%"
                    w="100%"
                  />
                </Box>
                <Stack>
                  <Text
                    color={"green.500"}
                    textTransform={"uppercase"}
                    fontWeight={800}
                    fontSize={"sm"}
                    letterSpacing={1.1}
                  >
                    <Tag>{data.tags}</Tag>
                  </Text>
                  <Heading fontSize={"2xl"}>{data.title}</Heading>
                  <Text color={"gray.500"} noOfLines={6}>{data.brief}</Text>
                </Stack>
                <Stack mt={6} direction={"row"} spacing={4} >
                  <Avatar
                    // src={"https://avatars0.githubusercontent.com/u/1164541?v=4"}
                    name="Achim Rolle"
                    alt={"Author"}
                  />
                  <Stack direction={"column"} spacing={0} fontSize={"sm"}>
                    <Text fontWeight={600}>Vaishali</Text>
                    <Text color={"gray.500"}>Feb 08, 2021 · 6min read</Text>
                  </Stack>
                </Stack>
              </Box>
            </>
          );
        })}
      </Box>
    </>
  );
};

export default BlogSection;

