import React from "react";
import { FormControl, Divider, Text, Flex, Box } from "@chakra-ui/react";
import {
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  MenuItemOption,
  MenuGroup,
  MenuOptionGroup,
  MenuDivider,
} from '@chakra-ui/react'

const FilterCard = () => {
  return (
    <>
      <Flex flexDirection={"column"}>
        <SortComponent />
        <FilterComponent />
      </Flex>
    </>
  );
};

export default FilterCard;

const SortComponent = () => {
  return (
    <>
      <FormControl p=".5rem">
        <Box
          display="flex"
          alignItems="center"
          rounded={"md"}
          border="1px solid lightgrey"
          p=".75rem"
        >
          <Text>Sort By :</Text>
          <select border="none" outline="none">
            <option border="none" bg="transparent" outline="none">
              <Text fontWeight={"extrabold"}>High to Low</Text>
            </option>
            <option border="none" bg="transparent" outline="none">
              <Text fontWeight={"extrabold"}>Low to High</Text>
            </option>
            <option border="none" bg="transparent" outline="none">
              <Text fontWeight={"extrabold"}>Rating</Text>
            </option>
          </select>
        </Box>
      </FormControl>
    </>
  );
};

const FilterComponent = () => {
  return (
    <Flex padding=".5rem" flexDirection={"column"}>
      <Box padding=".5rem">
        <Text fontWeight={"extrabold"}>FILTERS</Text>
        <Text fontSize="sm" color={"grey"}>
          1000+ Products
        </Text>
      </Box>
      <Divider colorScheme={"red"} />
    </Flex>
  );
};
