import React from "react";
import { ChakraProvider, Image, Box } from "@chakra-ui/react";
import { Pagination, Navigation, Keyboard, Autoplay } from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import "./carousel.css";

// images
import SafetyJacket from "../Image/temp-img/SafetyJacket.png";
import FootSole from "../Image/temp-img/FootSole.png";
import tape from "../Image/temp-img/tape.png";
import TShirt from "../Image/temp-img/TShirt.png";
import YellowTape from "../Image/temp-img/YellowTape.png";

const Herocarousel = () => {
  return (
    <>
      <ChakraProvider>
        <Box display="flex" justifyContent="center" border="1px solid red" pt>
          <Swiper
            style={
              {
                // "--swiper-pagination-color: yellow;
                // --swiper-theme-color: lightgrey;
                // --swiper-navigation-size: 1";
              }
            }
            breakpoints={{
              300: {
                slidesPerView: 1,
                spaceBetween: 20,
              },
              768: {
                slidesPerView: 1,
                spaceBetween: 20,
              },
              1024: {
                slidesPerView: 1,
                spaceBetween: 20,
              },
            }}
            slidesPerView={1}
            spaceBetween={30}
            loop={true}
            pagination={{
              clickable: true,
            }}
            autoplay={{
              delay: 2500,
              disableOnInteraction: false,
            }}
            keyboard={{
              enabled: true,
            }}
            navigation={true}
            modules={[Pagination, Navigation, Keyboard, Autoplay]}
            className="mySwiper"
          >
            <SwiperSlide>
              <Image src={FootSole} boxSize={["15%"]} />
            </SwiperSlide>
            <SwiperSlide>
              <Image src={SafetyJacket} boxSize={["15%"]} />
            </SwiperSlide>
            <SwiperSlide>
              <Image src={tape} boxSize={["15%"]} />
            </SwiperSlide>
            <SwiperSlide>
              <Image src={TShirt} boxSize={["15%"]} />
            </SwiperSlide>
            <SwiperSlide>
              <Image src={YellowTape} boxSize={["15%"]} />
            </SwiperSlide>
          </Swiper>
        </Box>
      </ChakraProvider>
    </>
  );
};

export default Herocarousel;
