import React from "react";
import { ChakraProvider, Image, Box } from "@chakra-ui/react";
import { Navigation, Keyboard, Autoplay } from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import "./carousel.css";

// images
import SafetyJacket from "../Image/temp-img/SafetyJacket.png";
import FootSole from "../Image/temp-img/FootSole.png";
import tape from "../Image/temp-img/tape.png";
import TShirt from "../Image/temp-img/TShirt.png";
import YellowTape from "../Image/temp-img/YellowTape.png";

const Herocarousel = () => {
  return (
    <>
      <ChakraProvider>
        {/* <Box
          borderBottom="1px solid"
          px={{ xl: "1rem", lg: "1rem", md: ".75rem", sm: ".60rem" }}
          py={{ xl: "1.75rem", lg: "1.5rem", md: "1rem", sm: ".60rem" }}
          textAlign={{
            sm: "center ",
            md: "center",
            lg: "center",
            xl: "center",
          }} */}
        {/* > */}
        <Swiper
          style={{
            // "--swiper-pagination-color": "yellow",
            "--swiper-theme-color": "lightgrey",
            "--swiper-navigation-size": " 2rem",
          }}
          slidesPerView={1}
          spaceBetween={20}
          loop={true}
          pagination={{
            clickable: true,
          }}
          autoplay={{
            delay: 2500,
            disableOnInteraction: false,
          }}
          keyboard={{
            enabled: true,
          }}
          navigation={true}
          modules={[Navigation, Keyboard, Autoplay]}
          className="mySwiper"
        >
          <Box
            border="1px solid"
            px={{ xl: "1rem", lg: "1rem", md: ".75rem", sm: ".60rem" }}
            py={{ xl: "1.75rem", lg: "1.5rem", md: "1rem", sm: ".60rem" }}
            display="flex"
            justifyContent="center"
            alignItems="center"
          >
            <SwiperSlide>
              <Image objectFit="cover" src={FootSole} h="auto" w="25%" />
            </SwiperSlide>
          </Box>
          <SwiperSlide>
            <Box
              border="1px solid"
              px={{ xl: "1rem", lg: "1rem", md: ".75rem", sm: ".60rem" }}
              py={{ xl: "1.75rem", lg: "1.5rem", md: "1rem", sm: ".60rem" }}
              display="flex"
              justifyContent="center"
              alignItems="center"
            >
              <Image objectFit="cover" src={SafetyJacket} />
            </Box>
          </SwiperSlide>
          <SwiperSlide>
            <Box
              border="1px solid"
              px={{ xl: "1rem", lg: "1rem", md: ".75rem", sm: ".60rem" }}
              py={{ xl: "1.75rem", lg: "1.5rem", md: "1rem", sm: ".60rem" }}
              display="flex"
              justifyContent="center"
              alignItems="center"
            >
              <Image objectFit="cover" src={tape} h="auto" w="25%" />
            </Box>
          </SwiperSlide>
          <SwiperSlide>
            <Box
              border="1px solid"
              px={{ xl: "1rem", lg: "1rem", md: ".75rem", sm: ".60rem" }}
              py={{ xl: "1.75rem", lg: "1.5rem", md: "1rem", sm: ".60rem" }}
              display="flex"
              justifyContent="center"
              alignItems="center"
            >
              <Image objectFit="cover" src={TShirt} h="auto" w="25%" />
            </Box>
          </SwiperSlide>
          <SwiperSlide>
            <Box
              border="1px solid"
              px={{ xl: "1rem", lg: "1rem", md: ".75rem", sm: ".60rem" }}
              py={{ xl: "1.75rem", lg: "1.5rem", md: "1rem", sm: ".60rem" }}
              display="flex"
              justifyContent="center"
              alignItems="center"
            >
              <Image objectFit="cover" src={YellowTape} h="auto" w="25%" />
            </Box>
          </SwiperSlide>
        </Swiper>
        {/* </Box> */}
      </ChakraProvider>
    </>
  );
};

export default Herocarousel;
