import React from "react";
import {
  FormControl,
  // Menu,
  Text,
  Select,
  FormLabel,
  Flex,
} from "@chakra-ui/react";

const FilterComponent = () => {
  return (
    <>
      <Flex padding=".5rem">
        <SortComponent />
      </Flex>
    </>
  );
};

export default FilterComponent;

const SortComponent = () => {
  return (
    <>
      <FormControl display="flex" alignItem="center" jus>
        <FormLabel>
          <Text fontSize={"md"}>Sort by :</Text>
        </FormLabel>
        <Select>
          <option> High to Low</option>
          <option> Low to High</option>
          <option>Rating</option>
        </Select>
      </FormControl>
    </>
  );
};
