import React, { useState } from "react";
import { Box, Flex } from "@chakra-ui/react";
import FilterCard from "./FilterComponent";
import CartItems from "./CartItems";

// context Api
import { CartState } from "../Context/context";

const FullComponent = () => {
  return (
    <>
      <Flex
        pos={"absolute"}
        flexDirection={["column"]}
        px="1rem"
        py="1rem"
        gap={"1rem"}
        top={{ xl: "90%", lg: "80%", md: "80%", base: "90%" }}
        w="100%"
      >
        <FilterPortion />
        {/* <CartItemsPortion /> */}
      </Flex>
    </>
  );
};

export default FullComponent;

const FilterPortion = ({ children, ...props }) => {
  return (
    <>
      <Flex
        zIndex={["1"]}
        {...props}
        position={["absolute"]}
        border={'1px solid'}
        bottom={["-%"]}
        // border="1px solid lightgrey"
        borderRadius={"md"}
        boxShadow={"base"}
        padding=".5rem"
        maxH={"90vh"}
      >
        <FilterCard />
      </Flex>
    </>
  );
};

const CartItemsPortion = ({ children, ...props }) => {
  const {
    state: { products },
    productState: { sort, byStock, category },
  } = CartState();
  const [RenderCategory, setRenderCategory] = useState(category);

  const FilterResult = (CatItems) => {
    const results = [...category].filter((currData) => {
      return currData.category === CatItems;
    });
    setRenderCategory(results);
  };

  const transformProducts = () => {
    let sortedProducts = products;

    if (sort) {
      sortedProducts = sortedProducts.sort((a, b) =>
        sort === "lowToHigh" ? a.price - b.price : b.price - a.price
      );
    }

    if (!byStock) {
      sortedProducts = sortedProducts.filter((prod) => prod.inStock);
    }
    if (FilterResult === true) {
      sortedProducts = sortedProducts.filter(
        (prod) => prod.category === category
      );
    }
    return sortedProducts;
  };
  return (
    <>
      <Box
        {...props}
        boxShadow="base"
        border="1px solid lightgrey"
        borderRadius={"md"}
        w={"80vw"}
        pos="relative"
      >
        <Box display={"flex"} flexWrap={"wrap"} justifyContent={"space-around"}>
          {transformProducts().map((prod) => (
            <CartItems prod={prod} key={prod.id} />
          ))}
        </Box>
      </Box>
    </>
  );
};
