import React, { useState, useEffect } from "react";
import Product from "./product.json";
import {
  Box,
  Image,
  Text,
  // useColorModeValue,
  Heading,
  Stack,
  Button,
} from "@chakra-ui/react";

import { Eye } from "@styled-icons/feather/Eye";

const CartItems = () => {
  const [Prod, setProd] = useState([]);
  const FetchPost = () => {
    let CalculatedProd = [];
    if (Product.length > 0) {
      for (let index = 0; index < 12; index++) {
        CalculatedProd = [...CalculatedProd, Product[index]];
      }
    }
    // console.log(CalculatedProd);

    setProd(CalculatedProd);
  };
  useEffect(() => {
    return () => {
      FetchPost();
    };
  }, []);

  return (
    <>
      <Box
        padding="1%"
        display="flex"
        flexWrap="wrap"
        justifyContent={"space-around"}
        alignItems="center"
      >
        {Prod.map((item, index) => {
          return (
            <Box key={index} margin="2%" border="1px solid">
              <Button
                // display="none"
                pos={"absolute"}
                aria-label="left-arrow"
                // left="40%"
                bg="red"
                // top={"40vmax"}
                // transform={"translate(0%, 50%)"}
                borderRadius="md"
                zIndex={2}
                // variant="ghost"
                // color="#fff"
                // _groupHover={{
                //   color: "white",
                //   bg: "orange.500",
                //   display: "block",
                // }}
              >
                <Eye size={24} />
              </Button>
              <Image
                src={item.images}
                height={230}
                width={282}
                objectFit={"cover"}
                zIndex=""
              />
              <Stack pt={5} align={"center"}>
                <Text
                  color={"gray.500"}
                  fontSize={"sm"}
                  textTransform={"uppercase"}
                >
                  {item.category.name}
                </Text>
                <Heading fontSize={"2xl"} fontFamily={"body"} fontWeight={500}>
                  Nice Chair, pink
                </Heading>
                <Stack direction={"row"} align={"center"}>
                  <Text fontWeight={800} fontSize={"xl"}>
                    ${item.price * 2}
                  </Text>
                  <Text textDecoration={"line-through"} color={"gray.600"}>
                    ${item.price}
                  </Text>
                </Stack>
              </Stack>
            </Box>
          );
        })}
      </Box>
    </>
  );
};

export default CartItems;
