import React from "react";
import {
  FormControl,
  Divider,
  Text,
  Flex,
  Box,
  Button,
  Tag,
  TagLabel,
  TagLeftIcon,
} from "@chakra-ui/react";
import {
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  // MenuItemOption,
  // MenuGroup,
  // MenuOptionGroup,
  // MenuDivider,
} from "@chakra-ui/react";
import { ChevronDownIcon, StarIcon } from "@chakra-ui/icons";

const FilterCard = () => {
  return (
    <>
      <Flex flexDirection={"column"}>
        <SortComponent />
        <FilterComponent />
      </Flex>
    </>
  );
};

export default FilterCard;

const SortComponent = () => {
  return (
    <>
      <FormControl p=".5rem">
        <Box
          display="flex"
          alignItems="center"
          rounded={"md"}
          border="1px solid lightgrey"
          p=".75rem"
        >
          <Text>Sort By :</Text>
          <select border="none" outline="none">
            <option border="none" bg="transparent" outline="none">
              <Text fontWeight={"extrabold"}>High to Low</Text>
            </option>
            <option border="none" bg="transparent" outline="none">
              <Text fontWeight={"extrabold"}>Low to High</Text>
            </option>
            <option border="none" bg="transparent" outline="none">
              <Text fontWeight={"extrabold"}>Rating</Text>
            </option>
          </select>
        </Box>
      </FormControl>
    </>
  );
};

const FilterComponent = () => {
  const PriceList = [
    "Under 149",
    "Under 199",
    "Under 249",
    "Under 299",
    "Under 349",
    "Under 399",
    "Under 449",
    "Under 499",
  ];
  return (
    <Flex padding=".5rem" flexDirection={"column"} gap=".5rem">
      <Box padding=".5rem">
        <Text fontWeight={"extrabold"}>FILTERS</Text>
        <Text fontSize="sm" color={"grey"}>
          1000+ Products
        </Text>
      </Box>
      <Divider colorScheme={"red"} />
      {/* pricing */}
      <Menu>
        <MenuButton
          as={Button}
          border="1px solid lightgrey"
          color={"blackAlpha.900"}
          rightIcon={<ChevronDownIcon />}
        >
          Price
        </MenuButton>
        <Box>
          <MenuList
            display={"flex"}
            flexWrap="wrap"
            flexDirection="column"
            gap="1rem"
            marginRight="1rem"
            MaxH="30vh"
          >
            {PriceList.map((item, index) => {
              return (
                <MenuItem>
                  <Tag key={index} variant="outline" rounded={"md"}>
                    <TagLabel>{item}</TagLabel>
                  </Tag>
                </MenuItem>
              );
            })}
          </MenuList>
        </Box>
      </Menu>
      {/* Rating */}
      <Menu>
        <MenuButton
          as={Button}
          bg={"white"}
          border="1px solid lightgrey"
          color={"blackAlpha.900"}
          rightIcon={<ChevronDownIcon />}
        >
          Rating
        </MenuButton>
        <Box>
          {[1, 2, 3, 4, 5].map((item, index) => {
            return (
              <Tag key={index} variant="subtle" colorScheme="">bal
                <TagLeftIcon boxSize="12px" as={StarIcon} />
                <TagLabel>{index}</TagLabel>
              </Tag>
            );
          })}
        </Box>
      </Menu>
    </Flex>
  );
};
