import { Box ,Button,Text } from "@chakra-ui/react";
// import "./trending.css";
import products from "../Data/data.json";

export default function App() {
  return (
    <Box >
      <Box display="flex" justifyContent={["center"]}  flexDirection={["column","row","row","row"]} gap={["1rem"]}>
        {products.map((product, i) => (
          <Product {...product} key={i} />
        ))}
      </Box>

      <div
        id="snipcart"
        data-api-key="NWMwZWNkZGMtZjU2ZS00YzM3LWFlZjYtMmM5Zjk0MWViZDcxNjM3Njg0OTY0ODg5NTk4MTM3"
        hidden
      ></div>
    </Box>
  );
}

function Product(props) {
  const { id, imageUrl, name, description, price } = props;

  return (
    <Box key={id} border="1px solid lightgrey" padding=".5rem" borderRadius={[".5rem"]} display="flex"  flexDir justifyContent={["center"]}>
      <img
        src={imageUrl}
        alt={`Image of ${name}`}
        className={"image-product"}
        h="auto"
        width={["60%","70%","80%","100%"]}
      />
      <Text pt={[".5rem"]}>{name}</Text>
      <Text pt={[".5rem"]}>{description}</Text>
      <span pt={[".5rem"]}>${price}</span>
      <div pt={[".5rem"]}>
        <Button
          className="snipcart-add-item"
          data-item-id={id}
          data-item-image={imageUrl}
          data-item-name={name}
          data-item-url="/"
          data-item-price={price}
        >
          Add to Cart
        </Button>
      </div>
    </Box>
  );
}
