import React from "react";
import Product from "./product.json";
import { Box, Image } from "@chakra-ui/react";

const CartItems = () => {
  // const [products, setProducts] = React.useState([]);
  // console.log(Product);
  const encodedParams = new URLSearchParams();
encodedParams.append("accessToken", "<REQUIRED>");
encodedParams.append("shopName", "<REQUIRED>");
encodedParams.append("applicationChargeId", "<REQUIRED>");

const options = {
	method: 'POST',
	headers: {
		'content-type': 'application/x-www-form-urlencoded',
		'X-RapidAPI-Key': 'SIGN-UP-FOR-KEY',
		'X-RapidAPI-Host': 'Shopifyvolodimir-kudriachenkoV1.p.rapidapi.com'
	},
	body: encodedParams
};

fetch('https://shopifyvolodimir-kudriachenkov1.p.rapidapi.com/activateApplicationCharge', options)
	.then(response => response.json())
	.then(response => console.log(response))
	.catch(err => console.error(err));
  return (
    <>
      {Product.map((product, index) => {
        return (
          <Box key={index}>
            <Box>{product.title}</Box>
            <Image src={product.image} />
          </Box>
        );
      })}
    </>
  );
};

export default CartItems;
